package june_29;

public class NextMultipleOfHundredExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Number=310;
		int nextmultiple,multiple;
		if(Number<=0)
		{
			System.out.println("Next Multiple:-1");
		}
		else 
		{
			multiple=(Number+100)/100;
			nextmultiple=multiple*100;
			System.err.println("nextmultiple:"+nextmultiple);
			

	}

}

	}


